INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (31, 4, 'Administrator', 2, 'SERGAP !!! Polres Serang Kota Menyisir Sejumlah Rumah Kos', 'sergap-polres-serang-kota-menyisir-sejumlah-rumah-kos', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kepolisian Resor Serang Kota melakukan razia tempat kos di wilayah cikulur, kecamatan ciracas, kota serang. Razia digelar berdasarkan laporan warga yang resah akan aktifitas para remaja pada malam hari yang kerap melakukan pesta minuman keras hingga pesta sex.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Selain menemukan belasan botol minuman keras dari kamar kos, Petugas juga mendapati pasangan muda-mudi yang berada dalam kamar dengan kondisi pintu terkunci.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Saat dilakukan pemeriksaan pasangan muda-mudi yang mayoritas merupakan mahasiswa dan mahasiswi ini tidak dapat menunjukan surat nikah sebagai bukti mereka pasangan suami-istri. menurut warga, para muda-mudi ini memang kerap kali melakukan pesta minuman keras dan pesta sex sehingga membuat warga sekitar resah, walau kerap kali dilakukan peneguran oleh warga namun para muda-mudi ini tidak pernah mengindahkan teguran warga.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Untuk mengantisipasi tindak anarki warga, tiga puluh muda-mudi yang terjaring razia ini langsung dibawa ke mapolres serang kota untuk dilakukan pendataan dan di serahkan kepada orang tua masing-masing agar kejadian serupa tidak kembali di ulangi.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Warga meminta pihak kepolisian dapat rutin melakukan razia seperti ini dan memberikan sanksi tegas agar para remaja jera dan tidak mengulangi perbuatan seperti ini di lingkungan kost-kosan. Kedepannya pihak kepolisian akan menggandeng satpol pp kota serang dan dinas sosial kota serang untuk tidak melakukan pembiaran terhadap penyakit masyarakat seperti ini.</span></span></p>\r\n', 1, '2019-05-15 03:01:00', '2019-05-15 03:01:56', 'news_2jd_17.jpg', 'https://www.youtube.com/watch?v=MnDE1netw4w', '04:35:00', '', 28015, NULL, 'polres serang kota', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (22, 1, 'Administrator', 2, 'Siti Tiba di Rumahnya di Serang-Banten', 'siti-tiba-di-rumahnya-di-serang-banten', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Puluhan awak media local, nasional dan internasional yang menunggu ke pulangan siti aisyah di kediamannya di kampung sindansari kecamatan pabuaran kabupaten serang banten, terpaksa harus terlibat adu dorong dengan warga sekitar. Siti aisyah tiba kediamannya di kawal ketat pihak kepolisian Polres Serang Kota</span></span></p>\r\n', 4, '2019-05-15 01:01:00', '2019-05-15 01:01:03', 'news_2b1_8.jpg', 'https://www.youtube.com/watch?v=IwNlu_BMf-I&amp;t=10s', '02:48:00', '', 424, NULL, 'kota serang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (23, 1, 'Administrator', 2, 'Rumah Siti Aisyah dijaga Ketat Pihak Kepolisian', 'rumah-siti-aisyah-dijaga-ketat-pihak-kepolisian', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Pengamanan dari petugas kepolisian dengan menggunakan senjata lengkap masih terlihat di sekitar lokasi kediaman siti aisyah, di kampung rancasumur, sindangsari, pabuaran kabupaten serang banten. sejumlah awak media yang hendak berbincang dengan siti aisyah pun masing menunggu di halaman rumah siti aisyah.</span></span></p>\r\n', 1, '2019-05-15 01:03:00', '2019-05-15 01:03:25', 'news_2vw_9.jpg', 'https://www.youtube.com/watch?v=g2DLI8Qt_Gk', '01:23:00', '', 957, NULL, 'banten,kota serang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (24, 1, 'Administrator', 2, 'Aksi Mahasiswa Sikapi Mobil Dinas Bupati Pandeglang', 'aksi-mahasiswa-sikapi-mobil-dinas-bupati-pandeglang', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Berlokasi di halaman kantor sekertaris daerah kabupaten pandeglang, puluhan mahasiswa menggelar aksi teaterikal memprotes kebijakan pemerintah kabupaten pandeglang yang menghamburkan uang hingga 1,9 milyar rupiah dengan membeli mobil bilup jenis landcruiser prado untuk kendaraan dinas bupati pandeglang irna narulita.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Para mahasiswa ini melakukan aksi teaterikal sebagai gambaran bagaimana kondisi masyarakat kabupaten pandeglang yang sulit untuk mendapat akses kesehatan karena infrastruktur yang buruk..</span></span></p>\r\n', 4, '2019-05-15 01:11:00', '2019-05-15 01:11:33', 'news_2w0_10.jpg', 'https://www.youtube.com/watch?v=3uoCx2MTK0c', '02:17:00', '', 9860, NULL, 'pandeglang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (25, 4, 'Administrator', 2, 'BNNP Banten Gagalkan Penyelundupan 10 Kilogram Sabu Asal Cina', 'bnnp-banten-gagalkan-penyelundupan-10-kilogram-sabu-asal-cina', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Pria berusia dua puluh sembilan tahun berinisial h-p warga bengkulu ini, nekat menyelundupkan sabu asal china seberat sepuluh kilogram yang dibawanya dari jakarta menuju lampung.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">H-P yang berprofesi sebagai sopir angkot ini mengaku jika dirinya tergiur dengan upah sepuluh juta rupiah yang diberikan kepadanya untuk mengambil dan mengantarkan sabu hingga ke tempat tujuan.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">H-P ditangkap petugas bbnp banten setelah dirinya diikuti berdasarkan informasi bahwa akan ada penyelundupan sabu dalam jumlah besar yang akan melintas di provinsi banten.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">berdasarkan informasi tersebut petugas berhasil mengamankan h-p yang sedang beristirahat di rest area tol balaraja tangerang-merak banten. Saat dilakukan pemeriksaan, petugas BNNP banten berhasil menemukan sepuluh kilogram sabu yang dikemas menggunakan almunium foil berlebel huruf china yang dilapisi plastik bening dan dikemas dalam sepuluh paket besar.</span></span></p>\r\n', 1, '2019-05-15 01:17:00', '2019-05-15 01:17:19', 'news_283_11.jpg', 'https://www.youtube.com/watch?v=8SS1CL4D-pY&amp;t=4s', '03:18:00', '', 865, NULL, 'bnn banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (26, 1, 'Administrator', 2, 'Prabowo Subianto Sapa Pendukungnya di Banten', 'prabowo-subianto-sapa-pendukungnya-di-banten', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Calon presiden prabowo subianto melakukan kunjungan ke sejumlah titik di provinsi banten. salah satunya melakukan peresmian gedung DPD gerindra provinsi banten. calon presiden yang berpasangan dengan sandiaga uno ini juga melakukan orasi politik di depan ribuan masa. dalam pidato politiknya, capres nomot urut dua ini kembali menyinggung sejumlah isu seperti banyaknya aset yang masih dikuasai pihak asing dan masih tinggi nya harga sejumlah kebutuhan pokok yang dianggap memberatkan rakyat.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dalam kesempatan tersebut prabowo juga berjanji jika nantinya jika terpilih akan secepatnya menurunkan harga kebutuhan pokok termasuk tarif listrik. prabowo juga meminta kepada para pendukungnya agar ikut mengawal surat suara mulai dari tempat pemungutan suara, untuk memastikan tidak ada kecurangan.</span></span></p>\r\n', 4, '2019-05-15 01:37:00', '2019-05-15 01:37:50', 'news_ra_12.jpg', 'https://www.youtube.com/watch?v=SMM1uLKsF58&amp;t=4s', '04:18:00', '', 18983, NULL, 'pilpres2019', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (27, 2, 'Administrator', 2, 'Disbudpar Cilegon Bersama Masyarakat Rintis Kampung Budaya', 'disbudpar-cilegon-bersama-masyarakat-rintis-kampung-budaya', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dalam rangka pelestarian dan pengembangan sejarah dan budaya Dinas pariwisata kota cilegon mengundang Para penggiat kampung kita Untuk Berkunjung ke kelurahan randakari kecamatan ciwandan kota cilegon.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Sejumlah seni dan budaya di jadikan sambutan untuk menyambut para penggiat kampung kita KPK Tanah tinggi Kota Tangerang dalam kesempatan kali ini, bnu jandi yang merupakan insiator kampung kita memaparkan strategi dan tekhnis juga memberikan motivasi kepada para warga.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Berbekal kekayaan sejarah kebudayaan Cilegon yang pernah ada, Tini selaku kepala bidang dinas pariwisata dan budaya kota cilegon terus aktif melakukan sosialisasi guna menumbuhkan kepedulian dimasyarakat untuk menyelamatkan kebudayaan Kota Cilegon.</span></span></p>\r\n', 0, '2019-05-15 01:44:00', '2019-05-15 01:44:32', 'news_1wz_13.jpg', 'https://www.youtube.com/watch?v=WY4_U1T3y-Q&amp;t=5s', '03:16:00', '', 1914, NULL, 'cilegon', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (28, 2, 'Administrator', 2, 'Disbudpar Kota Cilegon Gelar Saresehan Desa Budaya', 'disbudpar-kota-cilegon-gelar-saresehan-desa-budaya', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dinas Pariwisata dan Budaya Kota Cilegon Menggelar Saresehan Desa Budaya. Kegiatan tersebut di isi oleh sejumlah tokoh masyarakat dan inisator Kampung KPK tanah tinggi yakni Ibnu Jandi.</span></span></p>\r\n', 0, '2019-05-15 02:20:00', '2019-05-15 02:20:29', 'news_pm_14.jpg', 'https://www.youtube.com/watch?v=Uun6RBR3OAs', '01:31:00', '', 1378, NULL, 'cilegon', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (29, 1, 'Administrator', 2, 'Terkait Kerusakan Lingkungan, Warga Kembali Datangi Ombudsman', 'terkait-kerusakan-lingkungan-warga-kembali-datangi-ombudsman', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dalam perkembangan laporan soal dampak banjir dan lainnya yang dirasakan warga kampung tanah tinggi kota Tangerang, akibat dari pembangunan rel kereta listrik bandara soekarno hatta, senin pagi ibnu jandi kembali mendatangi ombudsman untuk memepertanyakan tindak lanjut dari hasil laporan sebelumnya.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Pada tanggal 8 november 2018, dirinya sudah melakukan pelaporan dan mendapatkan 6 point satu di antaranya adalah pembuatan drainase untuk meminimalisir banjir. ibnu jandi meminta ombudsman dapat segera merealisasikan kesepakatan yang sebelumnya sudah terbangun bersama pjka juga masrakat tanah tinggi kota Tangerang.</span></span></p>\r\n', 4, '2019-05-15 02:26:00', '2019-05-15 02:26:40', 'news_2vy_15.jpg', 'https://www.youtube.com/watch?v=MNmeZEOAN1o&amp;t=107s', '03:03:00', '', 14968, NULL, 'tangerang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (30, 1, 'Administrator', 2, 'Hujan Dan Angin Kencang Robohkan Tiang CCTV', 'hujan-dan-angin-kencang-robohkan-tiang-cctv', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hujan lebat diserta angin kencang membuat tiang kamera pengawas cctv di jalan raya abdul hadi ciwaru kota serang roboh dan menimpa sebuah minibus yang sedang melintas.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Beruntung sang sopir yang saat itu sedang berkendra dengan posisi bersandar, dapat selamat dari peristiwa maut tersebut.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sopir yang shok langsung dievakuasi warga ke klinik terdekat, untuk mendapatkan penanganan medis. diketahui tiang kamera pegawas jalan cctv setinggi 6 meter ini adalah milik polres serang kota.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Menurut keterangan warga di sekitar lokasi, tiang kamera cctv ini roboh akibat spanduk kampanye calon presiden yang dikaitkan pada tiang kamera cctv sehingga saat hujan di sertai angin kencang tiang kamera cctv tak kuat menopang beban dan roboh ke jalan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Peristiwa ini juga membuat arus lalu lintas dari arah cipocok menuju serang sempat ditutup lantaran minibus dan tiang yang menghalangi badan jalan. petugas kepolisian dan dinas pemadam kebakaran kota serang langsung terjun kelokasi untuk mengevakuasi minibus dan tiang yang roboh agar lalu lintas dapat kembali lancar.</span></span></p>\r\n', 0, '2019-05-15 02:57:00', '2019-05-15 02:57:56', 'news_2ir_16.jpg', 'https://www.youtube.com/watch?v=UxBJ4ZAJmO0', '02:12:00', '', 618, NULL, 'polres serang kota', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (21, 1, 'Administrator', 2, 'Keluarga Ucapkan Terimakasih Kepada Pemerintah Atas Kebebasan Siti Aisyah', 'keluarga-ucapkan-terimakasih-kepada-pemerintah-atas-kebebasan-siti-aisyah', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kediaman siti aisyah di kampung sindangsari, kecamatan pabuaran, kabupaten serang banten, terlihat masih sepi.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Sejumlah awak media yang mendengar kabar kebebasan siti aisyah pun telah mulai berdatangan untuk mendengar langsung kabar bahagia tersebut dari siti aisyah.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Menurut kabar dari keluarga dekatnya, asria dan benah yang merupakan orang tua dari siti aisyah telah berangkat ke jakarta untuk menjemput pulang siti aisyah ke kediamannya. para keluarga pun mulai terlihat bersiap menyambut kedatangan siti aisyah.</span></span></p>\r\n', 2, '2019-05-15 00:57:00', '2019-05-15 00:57:40', 'news_119_7.jpg', 'https://www.youtube.com/watch?v=__a3P4jqfZQ&amp;t=18s', '02:35:00', '', 395, NULL, 'kota serang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (15, 1, 'Administrator', 2, 'MENKUMHAM BANGUN LAPAS TANPA TEMBOK DI TANGERANG', 'polres-lebak-berhasil-ungkap-kasus-pembunuhan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Seluas Sembilan Hektar Tanah Milik Pemprov Dki Jakarta Yang Telah Di Hibakan Ke Kementrian Hukum Dan HAM Di Desa Ciangir Kabupaten Tangerang Banten Akan Di Bangun Lembaga Pemasyrakatan.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Dengan Konsep Open Camp Atau Tanpa Tembok Nantinya Lapas Tersebut Dapat Menampung Lima Ribu Warga Binaan Yang Telah Memenuhi Syarat Syarat Sesuai Dalam Peraturan Perundangan Undangan Yang Berlaku.</span></span></p>\r\n', 0, '2019-05-14 23:49:00', '2019-05-14 23:49:44', 'news_19o_1.jpg', 'https://www.youtube.com/watch?v=q6muv9pGt7o&amp;t=1s', '02:35:00', 'MENKUMHAM BANGUN LAPAS TANPA TEMBOK DI TANGERANG', 994, NULL, 'menkumham', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (16, 1, 'Administrator', 2, 'Tim Satgas Polda Banten Jemput Paksa 3 Dari 4 Tersangka Mafia Tanah', 'tim-satgas-polda-banten-jemput-paksa-3-dari-4-tersangka-mafia-tanah', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Sejumlah personil satuan tugas satgas anti mafia tanah Polda Banten, menjemput paksa tiga tersangka kasus mafia tanah di desa parigi kecamatan cikande kabupaten serang banten.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Tersangka yakni berinisal R-T bersama dua tersangka lain berinisal B-S dan H-I.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Penjemputan paksa dilakukan penyidik setelah ketiga tersangka mangkir dari panggilan. penyidik satgas anti mafia tanah atas kasus hukum penyerobotan tanah atau penguasaan tanah milik lima belar warga oleh tersangka secara sepihak. </span></span></p>\r\n', 0, '2019-05-15 00:21:00', '2019-05-15 00:21:42', 'news_1ls_2.jpg', 'https://www.youtube.com/watch?v=-V6QjOohzGc&amp;t=27s', '02:39:00', '', 36, NULL, 'mafia tanah,polda banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (17, 1, 'Administrator', 2, 'Jelang Pilpres, Polres Serang Kota Gelar Cipkon', 'jelang-pilpres-polres-serang-kota-gelar-cipkon', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Guna mengantisipasi kejahatan jalanan menjelang pemilihan legislatif dan pemilihan presiden 2019, Polres serang kota menggelar razia di sejumlah titik.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Tidak sedikit para pengendara yang mencoba melawan dan lari dari petugas saat di hentikan kendaraannya. </span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Dari hasil cipta kondusif ini, petugas mengamankan puluhan kendaraan yang tidak di lengkapi surat-surat. </span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Untuk memberikan efek jera terhadap para pengendara yang tidak taat pajak. petugas polres serang kota melakukan sistem tilang. </span></span></p>\r\n', 2, '2019-05-15 00:23:00', '2019-05-15 00:23:18', 'news_cu_3.jpg', 'https://www.youtube.com/watch?v=-HpMyF3nOj8&amp;t=16s', '01:58:00', '', 764, NULL, 'polres serang kota', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (18, 4, 'Administrator', 2, 'Sabhara Polda Banten Temukan Remaja Sedang Konsumsi Alkohol di Alun Alun Kota Serang', 'sabhara-polda-banten-temukan-remaja-sedang-konsumsi-alkohol-di-alun-alun-kota-serang', 'Y', '<p>Banyaknya adauan dari masyarakat kepada pihak kepolisian terkait banyaknya aktifitas remaja yang menkonsumsi alkohol pada malam hari di wilayah alun-alun kota serang, Sabhara Polda Banten Melakukan Patroli.</p>\r\n\r\n<p>Petugas Menemukan Beberapa Remaja yang sedang menkonsumsi alkohol.</p>\r\n\r\n<p>Untuk memberikan efek jera, para remaja ini di beri tindakan disiplin oleh petugas.</p>\r\n', 2, '2019-05-15 00:29:00', '2019-05-15 00:29:39', 'news_df_4.jpg', 'https://www.youtube.com/watch?v=LoJJXOmZHgY&amp;t=5s', '02:21:00', '', 1822, NULL, 'polda banten', 0);
