INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (20, 1, 'Administrator', 2, '1.200 Boks Berisi Surat Suara di terima KPU Lebak', '1-200-boks-berisi-surat-suara-di-terima-kpu-lebak', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Komisi Pemilihan Umum kabupaten Lebak sabtu dini hari, telah menerima surat suara sebanyak 1.200 boks yang berisikan 600.000 lembar surat suara DPR RI. Surat suara tersebut disimpan di padepokan pencak silat stadion pasir ona.</span></span></p>\r\n', 2, '2019-05-15 00:46:00', '2019-05-15 00:46:59', 'news_2ac_6.jpg', 'https://www.youtube.com/watch?v=43aldMzUSoo&amp;t=6s', '01:25:00', '', 651, NULL, '', 0);
