INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (34, 3, 'Administrator', 2, 'Tekan Angka Pengangguran, Pemkot Gelar Job Fair', 'tekan-angka-pengangguran-pemkot-gelar-job-fair', 'Y', '<p>Dalam upaya menekan angka pengagguran serta mempertemukan pihak perusahaan dengan para pencari kerja, Pemerintah Kota Kangerang mengadakan job fair 2019 di 13 kecamatan .</p>\r\n\r\n<p>Gelaran Job Fair tersebut merupakan rangkaian hari jadi kota tangerang yang ke 26 dan job fair ini merupakan kegiatan rutin yang di adakan setiap tahun oleh pemerintah kota tangerang.</p>\r\n', 0, '2019-05-15 03:12:00', '2019-05-15 03:12:25', 'news_2il_20.jpg', 'https://www.youtube.com/watch?v=ezntz_SLVzM', '01:38:00', '', 899, NULL, 'tangerang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (35, 3, 'Administrator', 2, 'Pemkot Kaji Bantaran Cisadene Untuk Destinasi Wisata', 'pemkot-kaji-bantaran-cisadene-untuk-destinasi-wisata', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Pemerintah kota tangerang yang di nahkodai arief wismansyah, terus melakukan inovasi demi tercapainya waterfront city yang menjadikan sungai sebagai titik penting pembangunan.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kini sungai yang membentang di kota seribu industri dan sejuta jasa ini, sedang dalam kajian oleh pemerintah secara paripurna.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Rencananya pemerintah akan melakukan penataan kepada bantaran kali cisadane untuk di jadikan destinasi wisata di 5 kecamatan yang ada. Kepala dinas lingkungan hidup kota tangerang engkos zarkasyi menjelaskan, dalam penataan kali cisadane ini akan dilakukan secara sinergi. masyarakat juga harus ikut serta dalam penataan kali cisadene ini.</span></span></p>\r\n', 0, '2019-05-15 03:15:00', '2019-05-15 03:15:21', 'news_1cw_21.jpg', 'https://www.youtube.com/watch?v=aFiIghHHjvc', '01:51:00', '', 17851, NULL, 'cisadane,tangerang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (36, 1, 'Administrator', 2, 'Nusantara Tauhid Doa Bersama Untuk Pemilu Damai', 'nusantara-tauhid-doa-bersama-untuk-pemilu-damai', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Bertempat di alun-alun banten lama, ratusan masyarakat dan ulama menggelar dzikir doa bersama dan khataman al quran. Kegiatan tersebut juga diikuti inisitor nusantara mengaji Muhaimin Iskandar.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dengan hikmat, ratusan ulama dan masyarakat ini terus memanjatkan doa doa/ dan melantunkan ayat-ayat suci al quran.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Salah satu tujuan dari kegiatan ini yakni untuk mendoakan bangsa indonesia agar terhindar dari perpecahan, terutama menjelang pelaksanaan pemilu, dimana banyak masyarakat terkotak-kotak karena perbedaan dalam pilihan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Doa bersama juga ditujukan agar pelaksaan pemilu yang akan di gelar 17 april mendatang dapat berjalan lancar/ jujur dan adil. Selain di banten, kegiatan doa bersama dan khataman al quran untuk pemilu damai juga akan di gelar di sejumlah titik di Indonesia.</span></span></p>\r\n', 0, '2019-05-15 03:24:00', '2019-05-15 03:24:53', 'news_2s2_22.jpg', 'https://www.youtube.com/watch?v=fe0CfKc6TMg', '02:25:00', '', 565, NULL, 'pilpres2019', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (37, 3, 'Administrator', 2, 'ASDP Gelar Diskusi Wanita dan Poros Maritim Dunia', 'asdp-gelar-diskusi-wanita-dan-poros-maritim-dunia', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dalam pelayaran pergi-pulang pelabuhan merak-bakauheni, diskusi tol laut sejarah nusantara menunjukkan bahwa perempuan indonesia disegani khususnya dalam hal kemaritiman.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Diskusi ini dilakukan dalam forum diskusi tol laut dan pahlawan wanita poros maritim dunia berlangsung di atas kapal ferry executive ASDP. Jauh sebelum keberadaan raden ajeng kartini, Connie Rahakundini bakrie president indonesian institute for maritime studies menyebut sosok ratu kalinyamat yang lahir tiga abad sebelumnya merupakan putri sultan trenggana yang lahir dengan nama retno kencono dianggap sebagai perempuan yang melampaui jamannya.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ratu kalinyamat adalah salah satu penanda masa kejayaan maritim nusantara, saat itu perempuan sudah memimpin armada laut nusantara yang berkekuatan ratusan kapal dan puluhan ribu prajurit untuk mengusir portugis dari malaka.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tema wanita dan poros maritim dunia yang menjadi pembasaan dalam perjalanan diskusi tol laut kali ini merupkan relevansi melihat ada sosok seperti ira puspadewi, direktur utama pt angkutan sungai danau dan penyeberangan ASDP Persero yang bisa dianggap sebagai ratu kalinyamat modern.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Diskusi ini bertepatan dengan peringatan hut asdp juga hari kartini. saat ini asdp memiliki 29 cabang, 243 rute, dan mengoperasikan 150 kapal di 35 pelabuhan di seluruh wilayah indonesia di bawah kepemimpin seroang perempuan ira puspadewi.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Selain pejabat asdp dan segenap pemangku kepentingan, diskusi hari ini juga diikuti oleh akademisi nd institute, pelajar dan mahasiswa tujuannya agar bisa memahami apa dan bagaimana implementasi poros maritim dan program tol laut presiden joko widodo. Sebagai salah satu pelaksana utama dari agenda poros maritim dunia dan konsep tol laut, asdp menerapkan 4 pilar operasional konektivitas menghubungkan wilayah-wilayah di indonesia.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Digitalisasi berupa penerapan sistem e-ticketing dan cashless transaction long distance ferry (ldf) mengangkut logistik dan juga ternak. sementara di bidang pariwisata asdp membangun international marina resort / labuan bajo / dan menciptakan destinasi wisata seperti dermaga eksekutif di pelabuhan merak serta bakauheni.</span></span></p>\r\n', 0, '2019-05-15 03:27:00', '2019-05-15 03:27:21', 'news_mx_23.jpg', 'https://www.youtube.com/watch?v=5XgTlDQ8oVA', '03:30:00', '', 16797, NULL, '', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (38, 4, 'Administrator', 2, 'Peredaran Sabu Dari Dalam Lapas Berhasil di Gagalkan', 'peredaran-sabu-dari-dalam-lapas-berhasil-di-gagalkan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dalam pengungkapan jaringan sindikat narkotika jenis sabu, BNNP Banten berhasil mengamankan lima orang tersangka yakni berinisal AH, YS dan NB yang merupakan warga cianjur jawa barat. Kedua tersangka lainyya berinisial MM dan FD merupakan warga dari serang banten. Penangkapan ini bermula dari adannya informasi dari masyarajat bahwa akan ada pengiriman barang jenis sabu dari cianjur jawa barat kelapas kelas satu tangerang banten</span></span></p>\r\n', 0, '2019-05-15 03:31:00', '2019-05-15 03:31:27', 'news_25x_24.jpg', 'https://www.youtube.com/watch?v=xxz8uv6FdYo', '03:32:00', '', 2755, NULL, 'bnn banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (39, 2, 'Administrator', 2, 'Tanggapan Burgerkill Tentang RUU Permusikan', 'tanggapan-burgerkill-tentang-ruu-permusikan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Tanggapan Burgerkill Tentang RUU Permusikan</span></span></p>\r\n', 2, '2019-05-15 03:33:00', '2019-05-15 03:33:43', 'news_1bs_25.jpg', 'https://www.youtube.com/watch?v=gUaQDZUTH2c', '03:09:00', '', 46069, NULL, 'burgerkill', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (40, 1, 'Administrator', 2, 'Warga Kembali Temukan Mayat didalam Karung', 'warga-kembali-temukan-mayat-didalam-karung', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Saat ini jenazah yang ditemukan di dalam karung rabu siang kemaren masih berada di kamar jenazah rumah sakit banten. Petugas belum berhasil menemukan identitas korban lantaran kondisi jenazah yang rusak. selain sidik jari yang sulit diambil, retina mata korban juga mengalami kerusakan. Berdasarkan hasil tim petugas forensik mempunyai kemiripan jenazah dalam karung sebelumnya, yang kedua ini terdapat banyak luka bekas kekerasan benda tumpul di tubuh korban.</span></span></p>\r\n', 0, '2019-05-15 03:39:00', '2019-05-15 03:39:22', 'news_2l0_26.jpg', 'https://www.youtube.com/watch?v=o1GDxP-u0hk', '01:53:00', '', 6739, NULL, 'kota serang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (41, 4, 'Administrator', 2, 'Polres Lebak Berhasil Ungkap Kasus Pembunuhan', 'polres-lebak-berhasil-ungkap-kasus-pembunuhan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dalam waktu 2 hari, Polres lebak banten berhasil mengungkap kasus penemuan mayat wanita yang di temukan mengambang di sungai ciujung.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Wanita yang memiliki tatto di bagian lengan, awalnya diduga tewas tenggelam saat mandi di aliran sungai ciujung.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Petugas mencium kejanggalan atas kematian wanita asal boyolali ini. Petugas langsung melakukan penyelidikan lebih mendalam dan memanggil sjumlah saksi yang saat itu berada di lokasi.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dari hasil penyelidikan, petugas berhasil menangkap pelaku yang tak lain adalah rekann wanitanya sendiri.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Pelaku wanita berinisial ss berusia 16 tahun ini mengakui perbuatanya. Dari pengakuan pelaku kepada penyidik.</span></span></p>\r\n', 0, '2019-05-15 03:41:00', '2019-05-15 03:41:55', 'news_pt_27.jpg', 'https://www.youtube.com/watch?v=SZLEFs9qn6U', '02:35:00', '', 6532, NULL, 'lebak banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (42, 4, 'Administrator', 2, 'Polres Pandeglang Bentuk Tim Khusus Ungkap 2 Mayat Dalam Karung', 'polres-pandeglang-bentuk-tim-khusus-ungkap-2-mayat-dalam-karung', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kepolisian resor pandeglang bekerjasama dengan ditkrimum polda banten membentuk tim khusus dalam mengungkap kasus pembunuhan dua laki-laki yang ditemukan tewas dalam karung di pesisir pantai kabupaten pandeglang banten.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kedua korban yang merupakan rekan kerja di salah satu tambang pasir di kabupaten lebak ini di ketahui terakhir kali terlihat bersama di sebuah penginapan di villa berkah kecamatan cikeusik. kabupaten pandeglang banten.</span></span></p>\r\n', 0, '2019-05-15 03:44:00', '2019-05-15 03:44:44', 'news_2sp_28.jpg', 'https://www.youtube.com/watch?v=XolfI-wCwQs', '02:25:00', '', 13952, NULL, 'banten,pandeglang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (43, 4, 'Administrator', 2, 'Polres Pandeglang Ringkus 2 Dari 6 Pelaku Pembunuhan Mayat Dalam Karung', 'polres-pandeglang-ringkus-2-dari-6-pelaku-pembunuhan-mayat-dalam-karung', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tim khusus yang dibentuk polisi reserse pandeglang berhasil meringkus 2 dari 6 tersangka pelaku pembunuhan dua pria yang tewas di dalam karung.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dua pelaku berinisial S dan B ini merupakan nahkoda dan anak buah kapal speed boat yang diketahui membantu dalam membuang para korban ke tengah laut.</span></span></p>\r\n', 2, '2019-05-15 03:48:00', '2019-05-15 03:48:40', 'news_c8_29.jpg', 'https://www.youtube.com/watch?v=Q_YyL4br23A', '02:37:00', '', 19890, NULL, 'banten,polres pandeglang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (44, 1, 'Administrator', 2, 'KPU Kabupaten Tangerang Musnahkan Belasan Ribu Surat Suara Rusak', 'kpu-kabupaten-tangerang-musnahkan-belasan-ribu-surat-suara-rusak', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dari hasil penyortiran pelipatan oleh pihak kpu daerah kabupaten tangerang, Kurang lebih 14 ribu surat suara dalam kondisi rusak. Guna menghindari penyalahgunaan, Pihak KPU mengambil tindakan untuk memusnahkan belasan ribu surat suara dengan cara di bakar.</span></span></p>\r\n', 0, '2019-05-15 03:51:00', '2019-05-15 03:51:35', 'news_pp_30.jpg', 'https://www.youtube.com/watch?v=8Mr_rI091jE', '01:55:00', '', 782, NULL, 'kpu tangerang,pilpres2019', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (45, 1, 'Administrator', 2, '2 TPS di Kota Serang dilakukan Pemungutan Suara Ulang', '2-tps-di-kota-serang-dilakukan-pemungutan-suara-ulang', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Terkait adanya temuan pelanggaran kode etik, dua tps di kota serang banten dilakukan pemungutan suara ulang. Salah satu di antaranya tps 024 ciloang sumur pecung kota serang. Jumlah pemilih yang hadir ke tps untuk melakukan pemilihan ulang terlihat lebih sedikit dibandingkan sebelumnya. Dari 255 daftar pemilih tetap, namun hanya seratus tujuh puluh pemilih yang datang untuk melakukan pemilihan ulang.</span></span></p>\r\n', 0, '2019-05-15 03:53:00', '2019-05-15 03:53:49', 'news_2ac_31.jpg', 'https://www.youtube.com/watch?v=36owaRF78Go', '01:53:00', '', 682, NULL, 'kpu kota serang,pilpres2019', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (46, 5, 'Administrator', 2, 'Bupati Mandailing Natal Mengundurkan Diri', 'bupati-mandailing-natal-mengundurkan-diri', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dahlan Hasan Nasution mengajukan surat permohonan berhenti dari jabatannya sebagai Bupati Mandailing Natal (Madina), Sumatera Utara. Dalam surat pengunduran dirinya, Dahlan mengaku kecewa atas hasil Pemilu. Dalam surat bertanggal 18 April 2019 itu, tertulis bahwa Pemilu di Madina berjalan aman lancar dan terkendali. Namun, hasilnya sangat mengecewakan dan tidak sesuai dengan yang diharapkan.</span></span></p>\r\n', 0, '2019-05-15 03:58:00', '2019-05-15 03:58:57', 'news_1jr_32.jpg', 'https://www.youtube.com/watch?v=OG7XIxEX3fk', '00:50:00', '', 4379, NULL, 'flashnews', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (47, 5, 'Administrator', 2, 'Di Anggap Hina Prabowo, Istri Andre Taulany di Laporkan', 'di-anggap-hina-prabowo-istri-andre-taulany-di-laporkan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Postingan Erin Taulany yang dinilai menghina Prabowo Subianto makin berbuntut panjang. Istri Andre Taulany itu resmi dipolisikan. Pihak pelapor adalah pengacara, M. Firdaus Oiwobo yang juga Ketua Ormas Relawan Militan Prabowo. Pengacara yang beberapa kali menangani artis itu memperkarakan pemilik akun Erintaulany pada pagi tadi, Minggu (21/4) di Polda Metro Jaya. Firdaus menuduh Erin Taulany melakukan pencemaran nama baik melalui media elektronik sebagaimana tertuang dalam laporan yang ia buat.</span></span></p>\r\n', 0, '2019-05-15 04:01:00', '2019-05-15 04:01:56', 'news_2hi_32.jpg', 'https://www.youtube.com/watch?v=oiDmMvfCYFw', '01:53:00', '', 56750, NULL, 'flashnews', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (48, 5, 'Administrator', 2, 'Teror Bom di Perayaan Paskah Sri Lanka', 'teror-bom-di-perayaan-paskah-sri-lanka', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Gereja dan hotel di Kota Kolombo, Sri Lanka diledakkan bom. Akibat teror ini ratusan orang tewas dan ratusan lainnya dilarikan ke rumah sakit. Bom tersebut meledak di tiga gereja dan tiga hotel di Ibu Kota Sri Lanka, Kolombo sekitar pukul 09.00 WIB, Minggu (21/4/2019). Bom meledak di saat umat Kristiani melakukan ibadah misa Paskah.</span></span></p>\r\n', 0, '2019-05-15 04:03:00', '2019-05-15 04:03:47', 'news_1wi_32.jpg', 'https://www.youtube.com/watch?v=g_yz5d2qWAs', '01:44:00', '', 148036, NULL, 'flashnews', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (49, 5, 'Administrator', 2, 'Tiga Nama Calon Sekda Banten Masih di Meja Jokowi', 'tiga-nama-calon-sekda-banten-masih-di-meja-jokowi', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Tiga nama calon Sekretaris Daerah (Sekda) Provinsi Banten masih berada di meja sekretariat negara (Setneg). Ketiganya antara lain Lili Romli, Al Muktabar dan Septo Kanaldi. Rencananya, pasca pemilihan umum 2019 ini, ketiga nama langsung diajukan ke meja Presiden Joko Widodo untuk dipilih salah satunya sebagai Sekda Banten. &ldquo;Belum mengerucut satu nama. Tiga-tiganya diajukan ke Presiden. Masih di Setkab. Jadi informasinya setelah Pilpres ini, kita tunggu aja,&rdquo; kata Kepala Badan Kepegawaian Daerah (BKD) Provinsi Banten Komarudin kepada awak medida, Senin (22/4/2019).</span></span></p>\r\n', 0, '2019-05-15 04:06:00', '2019-05-15 04:06:49', 'news_2kz_32.jpg', 'https://www.youtube.com/watch?v=z5ZMl3S6tGs', '01:34:00', '', 1220, NULL, 'flashnews', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (50, 5, 'Administrator', 2, 'Antisipasi Kelelahan Petugas PPK, Pemkab Tangerang Siapkan Tim Medis', 'antisipasi-kelelahan-petugas-ppk-pemkab-tangerang-siapkan-tim-medis', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Tigaraksa, Antisipasi kelelahan para petugas Panitia Pemilihan Kecamatan atau PPK yang tengah menggelar rekapitulasi perolehan suara Pemilihan Presiden dan Pemilihan Legislatif pemerintah Kabupaten Tangerang kerahkan petugas medis untuk pantau kesehatan petugas PPK. Ketua Desk Pemilu Kabupaten Tangerang Moch Maesal Rasyid perintahkan Dinas Kesehatan untuk mengerahkan Tim Medis mengawal jalannya Rapat Pleno Pemilihan Presiden dan Legislatif yang tengah digelar ditingkat Kecamatan.</span></span></p>\r\n', 0, '2019-05-15 04:09:00', '2019-05-15 04:09:14', 'news_2s2_32.jpg', 'https://www.youtube.com/watch?v=havirsPaEto', '01:34:00', '', 4589, NULL, 'flashnews', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (51, 5, 'Administrator', 2, '2 Kapal Bertabrakan, 1 ABK Hilang di Perairan Merak Bakauheni', '2-kapal-bertabrakan-1-abk-hilang-di-perairan-merak-bakauheni', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dua Kapal Motor Penumpang (KMP) Roll On Roll Off yakni Windu Karsa Dwitya bertabrakan dengan KMP Virgo 18 di jalur penyeberangan perlintasan Merak-Bakauheuni, Senin (22/4/2019). Peristiwa ini mengakibatkan Satu Anak Buah Kapal (KMP) Windu Karsa Dwitya bernama Rindang Roch Basuki terjatuh ke laut dan belum diketemukan. Kepala Kantor Pencarian dan Pertolongan (KPP) Banten Zaenal Arifin mengatakan kronologis kejadian ini terjadi sekitar pukul 16.30 WIB. Sebelum kejadian KMP Windu Karsa Dwitya berangkat dari Dermaga satu Pelabuhan Merak menuju Pelabuhan Bakauheuni, Lampung. Sedangkan KMP Virgo 18 dari arah sebaliknya yakni berangkat dari Pelabuhan Bakauheuni menuju Pelabuhan Merak. </span></span></p>\r\n', 0, '2019-05-15 04:28:00', '2019-05-15 04:28:29', 'news_29a_32.jpg', 'https://www.youtube.com/watch?v=_Biim7t5we8', '01:34:00', '', 74132, NULL, '', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (52, 1, 'Administrator', 2, '2 Kapal Terlibat Benturan di Merak, 1 Korban Masih Belum Di Temukan', '2-kapal-terlibat-benturan-di-merak-1-korban-masih-belum-di-temukan', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Beginilah peristiwa kecelakaan laut yang terjadi antara kapal windu karsa dwitya dan kapal penumpang virgo 18 di perairan selat sunda pada senin petang kemarin. Benturan yang cukup keras membuat lambung kapal beserta anjungan mengalami kerusakan berat. Benturan yang begitu keras membuat salah satu mualim kapal kmp windu karsa dwitya atas nama rindang terjatuh kelaut dari atas dek kapal yang hingga saat ini belum berhasil ditemukan/ dan masih dalam proses pencarian tim.</span></span></p>\r\n', 2, '2019-05-15 04:30:00', '2019-05-15 04:30:31', 'news_p6_34.jpg', 'https://www.youtube.com/watch?v=xBtzEnLn5E4', '02:22:00', '', 8976, NULL, 'merak', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (53, 5, 'Administrator', 2, 'Proses Pencarian Korban Peristiwa Laka 2 Kapal Saling Berbenturan di Merak', 'proses-pencarian-korban-peristiwa-laka-2-kapal-saling-berbenturan-di-merak', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Proses Pencarian Korban ABK atas nama Rindang oleh Basrnas Banten, Selasa (23/04/2019).</span></span></p>\r\n', 0, '2019-05-15 13:19:00', '2019-05-15 13:19:42', 'news_2vx_32.jpg', 'https://www.youtube.com/watch?v=DRgV304fAyg', '01:34:00', '', 10880, NULL, 'kecelakaan kapal,pelabuhan merak', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (54, 1, 'Administrator', 2, 'Terkendala Cuaca, Korban Laka Laut di Merak Banten Belum Di Temukan', 'terkendala-cuaca-korban-laka-laut-di-merak-banten-belum-di-temukan', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tim basarnas banten yang dibantu oleh angkatan laut dan polair serta pihak asdp melakukan penyisiran di lokasi jatuhnya mualim kapal kmp wdk atas nama rindang roch basuki.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Korban di ketahui terjatuh saat terjadinya kecelakaan laut antara kapal windu karsa dwitya dan kmp virgo 18 di break water dermaga tujuh pelabuhan merak.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Korban yang saat itu sedang berada diatas anjungan terjatuh ke laut bersama material kapal yang hancur akibat benturan keras.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Titik pencarian masih di fokuskan di sekitar lokasi tabrakan dengan radius 5 mil di sekitar pulau tempurung, pulau ular dan pulau sangiang.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Pencarian yang melibatkan empat regu ini belum juga membuahkan hasil. tingginya gelombang dan kencangnya angina diduga membuat jasad korban terbawa arus ke arah selatan.</span></span></p>\r\n', 2, '2019-05-15 13:22:00', '2019-05-15 13:22:42', 'news_nj_36.jpg', 'https://www.youtube.com/watch?v=mybx8DCAVAE', '02:09:00', '', 13015, NULL, 'laka laut merak banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (55, 1, 'Administrator', 2, 'Pihak Keluarga Berharap, Korban Dapat Segera Di Temukan', 'pihak-keluarga-berharap-korban-dapat-segera-di-temukan', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Pencarian korban laka laut kapal yang melibatkan basarnas banten, tni angkatan laut, polair masih terus dilakukan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tingginya gelombang dan kencangnya angin, menjadi penyebab sulitnya saat proses pencarian.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Titik pencarian di fokuskan pada radius lima mil dari lokasi jatuhnya korban, pihak keluarga yang ikut proses pencarian berharap jenazah korban dapat segera ditemukan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sementara tim pencarian masih terus melakukan pencarian dengan menyisir pantai di pulau sekitar lokasi jatuhnya korban.</span></span></p>\r\n', 2, '2019-05-15 13:27:00', '2019-05-15 13:27:00', 'news_n1_37.jpg', 'https://www.youtube.com/watch?v=RU9nLtnqXkY', '01:48:00', '', 7859, NULL, 'laka laut merak banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (56, 1, 'Administrator', 2, 'Korban Laka Laut Merak Berhasil di Temukan', 'korban-laka-laut-merak-berhasil-di-temukan', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Setelah selama dua hari melakukan pencarian, tim basarnas banten yang dibantu TNI Angkatan Laut dan Polairud Banten berhasil menemukan jasad rindang rochbasuki mualim kmp windu karsa dwitya yang jatuh ke laut saat terjadinya tabrakan kapal di water break, dermaga tujuh pelabuhan merak banten.</span></span></p>\r\n', 0, '2019-05-15 13:31:00', '2019-05-15 13:31:28', 'news_1b9_38.jpg', 'https://www.youtube.com/watch?v=jB32iAh5Ct0', '01:55:00', '', 13640, NULL, 'laka laut merak banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (57, 5, 'Administrator', 2, 'Jaga Kondusifitas, Polres Lebak Monitoring di Sejumlah TPS', 'jaga-kondusifitas-polres-lebak-monitoring-di-sejumlah-tps', 'Y', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Kepolisian Resort Lebak Banten, melakukan monitoring serta pengecekan terhadap Proses pemungutan suara ulang pemilu 2019 ditingkat Panitia Pemilihan Kecamatan (PPK) di dua TPS di antaranya TPS 13 Desa Pasar Keong dan TPS 9 Desa Bojong Cae Kec. Cibadak.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">&ldquo;Monitoring ini sebagai bentuk pelayanan Polri dalam mensukseskan pemilu 2019 yang aman,&rdquo; Kata Kabag Ops Polres Lebak, Kompol Adrian Tuuk, saat ditemui dilokasi, Rabu (24/04/2019).</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Dari hasil pengamatan dan monitoring dilapangan menunjukkan proses pemungutan surat suara DPRD Provinsi, DPRD Kabupaten dan DPD RI ditingkat kecamatan berjalan dengan baik.</span></span></p>\r\n', 0, '2019-05-15 13:38:00', '2019-05-15 13:38:54', 'news_1y2_32.jpg', 'https://www.youtube.com/watch?v=vsTLEsOd1Jk', '01:34:00', '', 9463, NULL, 'polres lebak banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (58, 5, 'Administrator', 2, 'Partainya di Hina Hanum Rais, Ini Balasan Tsamara', 'partainya-di-hina-hanum-rais-ini-balasan-tsamara', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hanum Rais, putri Ketua Dewan Kehormatan PAN Amien Rais, melontarkan pernyataan yang dinilai merendahkan Partai Solidaritas Indonesia (PSI).</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Kalimat menghina yang ia lontarkan di Twitter pada Rabu (24/4/2019) itu, berkaitan hasil Pemilu 2019, yang tak meloloskan PSI ke Senayan karena presentase suaranya tak memenuhi syarat.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hanum Rais memberikan julukan baru untuk PSI dalam tweet-nya.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">&quot;Partai NasaKom. Bukan Nasional Komunis lho. Tapi Partai Nasib Satu Koma,&quot; tulis @hanumrais.</span></span></p>\r\n', 0, '2019-05-15 13:45:00', '2019-05-15 13:45:30', 'news_1n0_39.jpg', 'https://www.youtube.com/watch?v=FrMtHG0zYmM', '01:58:00', '', 251136, NULL, 'partai psi', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (59, 5, 'Administrator', 2, 'Banten Kembali Raih Penghargaan Anugerah APE', 'banten-kembali-raih-penghargaan-anugerah-ape', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Provinsi Banten telah meraih penghargaan berupa Anugerah Parahita Ekapraya (APE) sebanyak 6 kali. Kondisi inilah yang menjadi bukti bahwa program-program pembangunan yang sudah dilaksanakan di Banten, memang dilakukan dengan berpedoman pada strategi pengarusutamaan gender.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">&ldquo;Untuk senantiasa terus meningkatkan peran-peran institusional perempuan dalam sektor publik khususnya melalui perencanaan pembangunan responsif gender,&rdquo; kata Wakil Gubernur Banten Andika Hazrumy dalam sambutannya pada acara pembukaan Rakornas Pemberdayaan Perempuan dan Perlindungan Anak 2019 di ICE BSD Tangerang, Rabu (24/04/2019).</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Acara yang dihadiri ratusan perwakilan Pemda se-Indonesia tersebut dibuka oleh Menteri Pemberdayaan, Perlindungan Perempuan dan Anak Yohana Yembise. Turut Hadir Menteri Desa, Pembangunan Daerah Tertinggal dan Transmigrasi Eko Putro Sandjojo.</span></span></p>\r\n', 0, '2019-05-15 13:49:00', '2019-05-15 13:49:21', 'news_bq_wagub_banten.jpg', 'https://www.youtube.com/watch?v=wAs28Ht8vV4', '01:34:00', '', 13513, NULL, 'provinsi banten,wagub banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (60, 1, 'Administrator', 2, 'Atas Kinerja Pencapaian Target PBB, Bupati Tangerang Apresiasi Kinerja Bapenda Dan Kecamatan', 'atas-kinerja-pencapaian-target-pbb-bupati-tangerang-apresiasi-kinerja-bapenda-dan-kecamatan', 'Y', '<p><span style=\"font-size:16px\">Dalam memperingati hari otonomi daerah, ratusan pegawai negri sipil melaksanakan apel pagi di lapangan maulana yuda tigarakasa kabupaten tangerang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Dalam kegiatan apel ini, bupati tangerang Zaki Iskandar mengapresiasi kinerja bapenda dan kecamatan yang dinilai bekerja keras dalam pencapaian target pajak bumi dan bangunan.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">pada tahun 2018 realisasi pbb mencapai kurang lebih tiga ratus tuujuh puluh dua milyar dari target tiga ratus milyar rupiah.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bupati berharap dengan pencapaian target 112, 7 persen ini dapat menjadi motivasi bagi dinas dan kecamatan juga kelurahan lain di wilayah kabupaten Tangerang.</span></p>\r\n', 4, '2019-05-15 13:51:00', '2019-05-15 13:51:48', 'news_1b9_zaki_iskandar.jpg', 'https://www.youtube.com/watch?v=2fYeY4oXVSc', '01:59:00', '', 16896, NULL, 'bupati tangerang,zaki iskandar', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (61, 1, 'Administrator', 2, 'Peringati Hari Buruh, Pemkab Tangerang Gelar Lomba Memancing', 'peringati-hari-buruh-pemkab-tangerang-gelar-lomba-memancing', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Berbeda dari lainnya, di kabupaten tangerang untuk memperingati hari buruh internasional ribuan buruh dari berbagai serikat, antusias mengikuti lomba memancing yang di gelar di danau tigarakasa kabupaten tangerang banten.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Kegiatan ini merupakan momen kebersamaan yang sudah terjalin antara para buruh dan pemerintah kabupaten Tangerang.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sekertaris Daerah Kabupaten Tangerang Moch Maesal Rasyid Mengatakan, Selain Memancing Kegiatan Lain Seperti Donor Darah Dan Melksanakan Olahraga Bersama Juga Juga Di Lakukan Dalam Memperingati Hari Buruh Internasional Ini.</span></span></p>\r\n', 2, '2019-05-15 13:55:00', '2019-05-15 13:55:12', 'news_26k_sekda_tangerang.jpg', 'https://www.youtube.com/watch?v=Hg6Aj05oq-M', '02:07:00', '', 98222, NULL, 'banten,hari buruh 2019,kabupaten tangerang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (62, 4, 'Administrator', 2, 'SAUR ON THE ROAD, SEJUMLAH REMAJA DI KOTA SERANG BENTROK', 'saur-on-the-road-sejumlah-remaja-di-kota-serang-bentrok', 'Y', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sejumlah remaja yang terlibat bentrok, berhasil di amankan oleh pihak kepolisian polres serang kota.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Para remaja ini di bawa ke mapolres serang kota untuk di berikan bimbingan oleh satuan reserse kriminal Polres Serang Kota dan Selanjutnya di kembalikan Kepada Pihak Keluarganya masing-masing.</span></span></p>\r\n', 0, '2019-05-15 13:58:00', '2019-05-15 13:58:50', 'news_d1_polres_serang_kota.jpg', 'https://youtu.be/Hg6Aj05oq-M', '04:48:00', '', 62565, NULL, 'polda banten,polres serang kota', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (63, 4, 'Administrator', 2, 'TEMPAT PROSTITUSI DI KOTA SERANG BEROPERASI DI BULAN RAMADHAN', 'tempat-prostitusi-di-kota-serang-beroperasi-di-bulan-ramadhan', 'Y', '<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Pada malam bulan ramadhan kepolisian resor serang kota gencar melakukan patroli guna memberantas penyakit masyarakat selama bulan suci Ramadhan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sejumlah tempat hiburan di kota serang tak luput dari pemeriksaan. Petugas melakukan pemeriksaan dan penggeledahan di setiap sudut tempat hiburan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Selain tempat hiburan warung penjual miras juga digeledah oleh petugas, alhasil ratusan botol miras berbagai merk ditemukan dan disita petugas dari para pemilik warung yang membandel.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tidak jauh dari loksi petugas kepolisian juga melakukan penyisiran ke sejumlah kios kosong di terminal kepandean yang dilaporkan warga dijadikan tempat prostitusi.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Saat dilakukan penggeledahan petugas mendapati sejumlah kios kosong yang terkunci dan telah dimodifikasi menjadi sebuah kamar.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dalam ruangan berukuran dua kali tiga meter ini terdapat sebuah kasur lantai yang terhalang kain yang diduga sebagai tempat melayani nafsu bejat para pria hidung belang, selainitu petugas juga mendapati alat kontrasepsi bekas pakai yang ditinggal oleh para pelaku.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Para wanita pekerja seks dan pria hidung belang ini diduga kabur saat mengetahui kedatangan petugas.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Pihak kepolisian akan menyampaikan temuan ini ke pemerntah kota serang agar dapat segera menindak lanjuti temuan ini serta menindak tegas para oknum yang memanfaatkan kios kosong di terminal kepandean untuk dijadikan tempat prostitusi.</span></span></p>\r\n', 0, '2019-05-15 14:02:00', '2019-05-15 14:02:02', 'news_xk_kasat_reskrim_kota_serang.jpg', 'https://www.youtube.com/watch?v=YHGodhjk5r8', '04:48:00', '', 93705, NULL, 'polres serang kota', 0);
