INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (94, 1, 'Administrator', 2, 'Bai Berumur Dua Bulan Menjadi Korban Kekerasan Ayah Kandung', 'bai-berumur-dua-bulan-menjadi-korban-kekerasan-ayah-kandung', 'Y', '<p>Saat ini kondisi ibu dan bainya mengalami syok berat dan terdapat sejumlah luka di bagian tubuh sang bai, akibat kekerasan yang dilakukan oleh ayah kandungnya sendiri.</p>\r\n\r\n<p>Atas kejadian ini, pihak keluarga sudah melaporkan ke pihak berwajib agar dapat di adili dengan hukuman yang setimpal.</p>\r\n', 2, '2019-06-19 20:59:12', '2019-06-19 20:59:12', 'news_1ap_serang_banten.jpg', 'https://www.youtube.com/watch?v=3e2m_ax7dKE', '03:09:00', '', 545, NULL, 'kota serang,peristiwa', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (95, 4, 'Administrator', 2, 'SERGAP - Polisi Grebek Tempat Produksi Minuman Keras di Tangerang Banten', 'sergap-polisi-grebek-tempat-produksi-minuman-keras-di-tangerang-banten', 'Y', '<p>Polisi berhasil mendapatkan barang bukti dari dua tempat lokasi yang memproduksi minuman keras berjenis ciu. Pelaku Berinisoal KV juga di amanakan. Dari informasi perangkat daerah, keberadaan rumah yang membuat minuman keras tersebut sangat meresahkan. Minuman keras ciu tersebut juga membuat dua warga meninggal dunia setelah menkonsumsi minuman tersebut.</p>\r\n', 2, '2019-06-20 12:44:00', '2019-06-20 12:44:33', 'news_2x3_pabrik_minuman_keras_di_tangerang_banten_di_grebek_polisi.jpg', 'https://www.youtube.com/watch?v=dW8HaLZEUs8', '10:30:00', '', 11849, NULL, 'kabupaten tangerang,sergap', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (96, 1, 'Administrator', 2, 'Puluhan Warga Keracunan', 'puluhan-warga-keracunan', 'Y', '<p>Dari hasil observasi pihak medis, puluhan warga ini di pastkan mengalami keracunan setelah menkonsusmsi ikan pindang.</p>\r\n\r\n<p>Dari 40 warga, 3 di antaranya di rujuk ke rumah sakit umum daerah serang guna dilakukan penanganan medis lebih lanjut dan 37 warga lainnya sementara di rawat di posko darurat.</p>\r\n\r\n<p>Keracunan yang di rasakan oleh puluhan warga ini, hampir keseluruhan mengalami gejala sakit kepala/ mual serta demam.</p>\r\n', 0, '2019-06-20 20:19:00', '2019-06-20 20:19:21', 'news_nx_puluhan_warga_kota_serang_keracunan.jpg', 'https://www.youtube.com/watch?v=uiGTvI4s0tI', '02:26:00', '', 494, NULL, 'kota serang', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (97, 1, 'Administrator', 2, 'Polda Banten Gelar Festival Millennial Road Safety Deklarasi Kebhinekaan', 'polda-banten-gelar-festival-millennial-road-safety-deklarasi-kebhinekaan', 'Y', '<p>Guna Meningkatkan Rasa Kesatuan Dan Kesadaran Dalam Menolak Provokasi,Polda Banten Menggelar Festival Damai Dalam Millennial Road Safety Yang Akan Dilaksanakan Pada Minggu 23 Juni 2019, Di Alun-Alun Tigaraksa Kabupaten Tangerang Banten.</p>\r\n', 2, '2019-06-23 02:18:00', '2019-06-23 02:18:17', 'news_y0_millennial_road_safety_polda_banten.jpg', 'https://www.youtube.com/watch?v=Rm7FAYrIQ7g', '02:12:00', '', 863, NULL, 'millennial road safety,polda banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (98, 1, 'Administrator', 2, 'Puluhan Ribu Peserta Padati Millennial Road Safety Festifval di Tangerang', 'puluhan-ribu-peserta-padati-millennial-road-safety-festifval-tangerang', 'Y', '<p>Puluhan ribu Peserta dari berbagai kalangan terlihat antusias mengikuti kegiatan yang digelar oleh polda banten.</p>\r\n\r\n<p>Millenial road festival yang berlangsung di alun-alun tigaraksa kabupaten tangerang di meriahkan dengan berbagai seni budaya dan sejumlah artis ibu kota.</p>\r\n\r\n<p>Kegiatan yang bertajuk merajut persatuan dalam kebhinekaan mendapatkan pesan positif dari masyarakat.</p>\r\n', 2, '2019-06-23 16:44:00', '2019-06-23 16:44:13', 'news_27m_puluhan_warga_padati_millennial_road_safety_festifval_tangerang.jpg', 'https://www.youtube.com/watch?v=b7WzuseAc90', '01:15:00', '', 1518, NULL, 'millennial road safety festival,polda banten', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (99, 1, 'Administrator', 2, 'Merajut Kesatuan Melalui Millennial Road Safety Festival', 'merajut-kesatuan-melalui-millennial-road-safety-festival', 'Y', '<p>Kapolda Banten Irjen Pol Tomsi Tohir menjelaskan, di Provinsi Banten sebanyak tujuh ratus lima puluh lima menjadi korban kecelakaan lalu lintas yang meninggal dunia, sementara seribu delapan ratus mengalami luka berat.</p>\r\n\r\n<p>Kapolda Banten berharap, dengan adanya kegiatan millennial road safety festifval dapat mengurangi angka kecelakaan dengan cara menjadi pelopor keselamatan dalam berlalu lintas.</p>\r\n', 2, '2019-06-23 21:10:00', '2019-06-23 21:10:36', 'news_2vc_polda_banten_road_festival.jpg', 'https://www.youtube.com/watch?v=Q59Or9Phx6I', '02:32:00', '', 1297, NULL, 'millennial road safety festival,polda banten', 0);
