INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (220, 1, 'Administrator', 2, 'Gubernur Berharap Singkong Bisa di Kembangkan Menjadi Komoditas Agrobisnis', 'gubernur-berharap-singkong-bisa-di-kembangkan-menjadi-komoditas-agrobisnis', 'Y', '<p><strong>SERANG</strong> - Gubernur Banten Wahidin Halim (WH) mengungkapkan, selama ini masyarakat memanfaatkan singkong sebagai makanan pengganti nasi. Singkong turut menjadi penopang ketahanan pangan masyarakat.</p>\r\n\r\n<p>&quot;Selama ini, singkong bisa menjadi makanan pengganti nasi (beras, red). Bisa diolah menjadi bermacam-macam aneka makanan,&quot; ungkap Gubernur saat melakukan Panen Singkong di Kawasan Sistem Pertanian Terpadu (Sitandu) Dinas Pertanian Provinsi Banten Jl Syech Nawawi Al-Bantani, Kp Kembang, Curug, Kota Serang (Rabu, 02/06/2021)</p>\r\n\r\n<p>Meski demikian, lanjut Gubernur, dirinya tidak berharap singkong menjadi makanan utama masyarakat.</p>\r\n\r\n<p>&quot;Nasi atau beras tetap menjadi makanan utama masyarakat,&quot; ungkapnya.</p>\r\n\r\n<p>Gubernur juga berharap komoditas singkong bisa dikembangkan seperti komoditas agrobisnis lainnya.</p>\r\n\r\n<p>Di Kawasan Sitandu, saat ini Dinas Pertanian Provinsi Banten mengembangkan singkong mentega, singkong koneng, singkong thailand, dan singkong roti. Di lahan ini, satu batang pohon singkong mampu menghasilkan 4 - 6 kg.</p>\r\n\r\n<p>Dalam kesempatan itu, turut pula ditampilkan aneka olahan pangan berbahan dasar singkong. Di antaranya getuk, ketimus, comro, misro, serta singkong rebus thailand.</p>\r\n\r\n<p> </p>\r\n\r\n<p>*Red</p>\r\n', 0, '2021-06-03 03:59:00', '2021-06-03 03:59:22', 'news_ne_whatsapp_image_2021_06_03_at_03.56.58.jpeg', 'Gubernur Banten Saat Panen Singkong', '00:00:00', '', 841, NULL, 'gubernur banten,pemprov banten,wahidin halim', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (221, 1, 'Administrator', 2, 'Detik-Detik Penemuan Jasad Korban Tenggelam di Pantai Anyer', 'detik-detik-penemuan-jasad-korban-tenggelam-di-pantai-anyer', 'Y', '<p><strong>SERANG</strong> - Detik-detik Petugas Gabungan Bersama dengan masyarakat setempat berhasil menemukan jasad yang tenggelam pada pada Rabu (02/06), pukul 15.00 WIB.</p>\r\n\r\n<p>Diketahui Korban bernama Ikhsan Maulana Rama berumur 15 Tahun, Pelajar asal Cipondoh Makmur Kota Tangerang.</p>\r\n\r\n<p>Korban ditemukan oleh Petugas tidak kauh dari lokasi kejadian.</p>\r\n\r\n<p>Petugas menemukan Korban sudah dalam keadaan tidak bernyawa.</p>\r\n\r\n<p>&ldquo;Sekitar jam 22.38 korban ditemukan dengan kondisi meninggal dunia, 200 meter dari TKP,&quot; kata Kepala Badan SAR Banten M Zainal kepada awak media, Rabu (02/06/21).</p>\r\n\r\n<p>*Red</p>\r\n', 0, '2021-06-03 04:14:10', '2021-06-03 04:14:10', 'news_1dp_bocah.jpg', 'https://www.youtube.com/watch?v=jneqQAIQ5w0 ', '01:13:00', '', 1840, NULL, 'korban tenggelam,peristiwa,pantai anyer', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (223, 8, 'Administrator', 2, 'ATM KPIN, Solusi Siswa Belajar Jarak Jauh', 'atm-kpin-solusi-siswa-belajar-jarak-jauh', 'Y', '<p><strong>TANGERANG</strong> - Bupati Tangerang A. Zaki Iskandar meninjau Dashboard Pendidikan, Kios Pintar Digital (ATM KIPIN) Di Teluknaga tepatnya di SDN 2 Teluknaga dan SDN Kebon Cau 2, Rabu (02/06/21).</p>\r\n\r\n<p>Ia berkesempatan meninjau bantuan ATM KIPIN dari PT Bumi resources (Bakrie group) untuk Sekolah Dasar di Kabupaten Tangerang dengan model alat atau aplikasi dan juga unit set dari KPIN yaitu perpustakaan digital yang sangat membantu sekali program pembelajaran jarak jauh di Kabupaten Tangerang.</p>\r\n\r\n<p>Alat ini, Kata Zaki, Dapat mempermudah para guru dan siswa untuk mendapatkan modul-modul pelajaran dari buku-buku yang tersedia di aplikasi KIPIN. Langkah kedepan, Pihaknya akan menambah ke seluruh sekolah SD dan SMP Negeri di Kabupaten Tangerang secara bertahap.</p>\r\n\r\n<p>&quot;Yang paling penting adalah alat yang sekarang digunakan ini sangat membantu untuk proses pembelajaran jarak jauh di tengah pandemi covid 19 ini dan diharapkan akan menjadi transformasi proses adaptasi kepada generasi penerus kita terhadap penggunaan dan kemajuan teknologi telekomunikasi informasi dan digitalisasi di dunia pendidikan,&quot; Kata Ahmed Zaki Iskandar, Bupati Kabupaten Tangerang, Rabu (02/06/21).</p>\r\n\r\n<p>Sukri selaku pengembang KIPIN mengatakan alat ini dinamakan KIPIN ATM atau mesin perpustakaan digital untuk membantu pembelajaran siswa siswa, Kegunaan KPIN ini adalah tersedianya buku-buku pelajaran sekolah di tingkatan SD, SMP, SMA, SMK. Selain itu, KPIN juga terdapat video-video pembelajaran pembahasan serta ada soal-soal try out dan literasi literasi serta komik komik untuk anak-anak.</p>\r\n\r\n<p>&quot;Jadi fungsi utama KIPIN adalah berisi konten- konten untuk siswa dan guru secara gratis karena konten yang ada di sini dapat dipindahkan ke handphone tablet secara langsung tanpa internet sama sekali karena sudah tersedia wifi di dalam dashboard KIPIN tersebut, jadi tidak perlu membutuhkan kuota atau internet,&quot; Ungkapnya.</p>\r\n\r\n<p>*Red</p>\r\n', 0, '2021-06-03 07:30:00', '2021-06-03 07:30:35', 'news_190_20210603_072950.jpg', 'Bupati Tangerang A. Zaki Iskandar meninjau Dashboard Pendidikan', '00:00:00', '', 754, NULL, 'kabupaten tangerang,zaki iskandar', 0);
INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (224, 1, 'Administrator', 2, 'Kejari Serang Musnahkan Belasan Ribu Barang Bukti Hasil Pengungkapan Kasus', 'kejari-serang-musnahkan-belasan-ribu-barang-bukti-hasil-pengungkapan-kasus', 'Y', '<p><strong>SERANG</strong> - Kejaksaan Negeri Serang melakukan pemusnahan barang bukti dari 69 perkara tindak pidana umum.</p>\r\n\r\n<p>Barang bukti yang dimusnahkan berupa narkotika jenis sabu seberat 97,56 gram dan 23 bungkus, tembakau ganja seberat 2.757.07 gram dan 14.241 Jamu Ilegal.</p>\r\n\r\n<p>Sebagian besar barang bukti yang dimusnahkan adalah berasal dari barang bukti yang disita selama tahun 2021.</p>\r\n\r\n<p>Dengan adanya penyitaan dan pemusnahan barang bukti tersebut diharapkan dapat memberikan efek jera bagi para pelaku.</p>\r\n\r\n<p>&ldquo;Barang bukti dari tahun 2021 dan ada juga yang dari tahun 2020 akhir yang belum sempat dimusnahkan. Tapi sebagian besar barang bukti tahun 2021. Kalau untuk penyitaannya melalui Pengadilan Negeri,&rdquo; Kata Kepala Kejari Serang, Supardi, Kamis (03/06/2021).<br />\r\n-----------------------------------------------------<br />\r\nHomepage : www.lenteranewstv.id</p>\r\n\r\n<p>SUBSCRIBE LenteraNEWS Official Youtube Channel : http://bit.ly/2KBqE1V</p>\r\n\r\n<p>Follow Us On Social Media</p>\r\n\r\n<p>Like us on Facebook : http://bit.ly/2KEeXra</p>\r\n\r\n<p>Follow us on Twitter : http://bit.ly/2X1xM9F</p>\r\n\r\n<p>Follow lenteranews.tv on Instagram : http://bit.ly/2Lgnccx</p>\r\n', 0, '2021-06-03 21:00:53', '2021-06-03 21:00:53', 'news_2sn_kajari_serang.jpg', 'https://www.youtube.com/watch?v=SLDTwdEMe0o ', '01:19:00', '', 8332, NULL, 'kejari serang,kota serang', 0);
