INSERT INTO `posting` (`id_post`, `id_cat`, `alias`, `id_publisher`, `judul`, `judul_seo`, `publish`, `postingan`, `status`, `tanggal`, `folder`, `gambar`, `youtube`, `durasi`, `caption`, `dibaca`, `count`, `tag`, `label`) VALUES (274, 8, NULL, 2, 'Muncul Corona Varian Baru, Permintaan Kelapa Muda Meningkat Tajam', 'muncul-corona-varian-baru-permintaa-kelapa-muda-meningkat-tajam', 'Y', '<p><strong>SERANG</strong> – Semenjak Corona memilik varian Baru, Permintaan Kelapa Muda Merah Melonjak Tajam. Para Pedagang Kelapa Muda Merah mengaku kewalahan untuk menyediakan stoknya.</p>\r\n\r\n<p>“Kelapa Muda Merah yang banyak di cari. Semenjak ada varian baru corona. Sehari 200 Kelapa aja terkadang Kurang. Nyari juga agak susah,” Kata Lehman, Pedagang Kelapa di Pasar Lama, Sabtu (10/09/2021).</p>\r\n\r\n<p>Menurut beberapa sumber, Air Kelapa mampu mengurangi risiko penyakit jantung, bisa menurunkan kolesterol, trigliserida, hingga mengurangi perlemakan hati.</p>\r\n\r\n<p>“Kalo pembeli biasanya buat nambah imun badan bilangnya.” Ujarnya.</p>\r\n\r\n<p>Tingginya permintaan ditengah mewabahnya virus corona varian baru, Membuat kelapa muda merah mengalamin kenaikan harga.</p>\r\n\r\n<p>“Klo dulu kelapa merah jual Rp 10 rb, kalo kelapa hijau Rp 8.000. Sekarang yang Kelapa Merah jadi Rp 15 Rb, Kelapa hijau Rp. 10 Rb.” Jelasnya.</p>\r\n\r\n<p>Potensi lain manfaat Air Kelapa adalah Anti Trombosis. Air Kelapa mampu mencegah penggumpalan darah hingga mengurangi stress oksidatif secara signifikan.</p>\r\n', 0, '2021-07-11 00:17:00', '2021-07-11 00:17:00', 'eb4662bf09ea446fbe9ebf933810ad15.jpg', '', '00:00:00', 'Permintaan Kelapa Hijau Meningkat Drastis, Hal ini terjadi setelah adanya Corona Varian Baru', 1, NULL, 'covid 19', 0);
